jQuery(document).ready(function(){jQuery("form.newsletter_manager").submit(function(){if(!this.from.value.match(/^\S+@\S+\.\S+$/)){return false;}var ok=true;jQuery(this).find(":text").each(function(){var t=jQuery(this);if(!t.val()||t.val()==t.attr("title")){ok=false;}});return ok;});jQuery("form.newsletter_manager :text").focus(function(){var t=jQuery(this);if(t.val()==t.attr("title")){t.val("");}});jQuery("form.newsletter_manager :text").blur(function(){var t=jQuery(this);if(t.val()==""){t.val(t.attr("title"));}});});